    /update Time:
    /update "F"$AltitudeMeters,"F"$DistanceMeters,"T"$Time from `telemetry;

    /exec HeartRateBpm from telemetry
    show distinct -6#'telemetry`Time; // Write timezone offsets(or end of datetime if no offest) in file to console.
    telemetry
    };
